package edu.austral.ingsis.gradle.app;

import edu.austral.ingsis.gradle.list.LinkedList;

import static edu.austral.ingsis.gradle.utilities.StringUtils.join;
import static edu.austral.ingsis.gradle.utilities.StringUtils.split;
import static edu.austral.ingsis.gradle.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
